class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        List<Integer>[] covered = new List[nums.length];
        for (int i = 0; i < nums.length; i++) {
            covered[i] = new ArrayList<>();
        }
        for (int i = 0; i < queries.length; i++) {
            int[] q = queries[i];
            for (int l = q[0]; l <= q[1]; l++) {
                covered[l].add(i);
            }
        }
        int min = -1;
        for (int i = 0; i < nums.length; i++) {
            int currMin = minZero(nums[i], covered[i], queries);
            if (currMin == -1) {
                return -1;
            }
            min = Math.max(min, currMin);
        }
        return min;
    }

    private int minZero(int target, List<Integer> covered, int[][] queries) {
        if (target == 0) {
            return 0;
        }
        boolean[] dp = new boolean[target + 1];

        for (int ind : covered) {
            int val = queries[ind][2];
            if (val > target) {
                continue;
            }
            boolean[] ndp = dp.clone();
            for (int j = 0; j + val < dp.length; j++) {
                if (dp[j] && j + val < dp.length) {
                    ndp[j + val] = true;
                }
            }
            ndp[val] = true;
            if (ndp[target]) {
                return ind + 1;
            }
            dp = ndp;
        }

        return -1;
    }
