class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        bool allGood = true;
        for (int i : nums) {
            if (i) {
                allGood = false;
                break;
            }
        }
        if (allGood) {
            return 0;
        }
        vector <vector <int>> numbers(nums.size());
        vector <int> sums(nums.size());
        for (int i = 0; i != queries.size(); ++i) {
            for (int j = queries[i][0]; j <= queries[i][1]; ++j) {
                numbers[j].push_back(queries[i][2]);
                sums[j] += queries[i][2];
            }
            allGood = true;
            for (int j = 0; j != nums.size(); ++j) {
                if (sums[j] < nums[j]) {
                    allGood = false;
                    break;
                }
                memset(DP, false, sizeof(DP));
                DP[0] = true;
                for (int k : numbers[j]) {
                    for (int x = nums[j]; x >= k; --x) {
                        DP[x] |= DP[x - k];
                    }
                }
                if (!DP[nums[j]]) {
                    allGood = false;
                    break;
                }
            }
            if (allGood) {
                return i + 1;
            }
        }
        return -1;
    }
};

