class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        def chk(k):
            for i, t in enumerate(nums):
                if t == 0: continue
                dp, m = 1, (1 << (t + 1)) - 1
                for j in range(k):
                    l, r, v = queries[j]
                    if l <= i <= r:
                        dp |= (dp << v) & m
                if not ((dp >> t) & 1):
                    return False
            return True

        lo, hi = 0, len(queries) + 1
        while lo < hi:
            mid = (lo + hi) // 2
            if chk(mid):
                hi = mid
            else:
                lo = mid + 1

        return lo if lo <= len(queries) else -1

