class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        if sum(nums) == 0:
            return 0

        dp = []
        for i in range(n):
            target = nums[i]
            temp = [False] * (target + 1)
            temp[0] = True
            dp.append(temp)

        for k, (l, r, val) in enumerate(queries):
            for i in range(l, r + 1):
                target = nums[i]
                for s in range(target - val, -1, -1):
                    if dp[i][s]:
                        dp[i][s + val] = True
            if all(dp[i][nums[i]] for i in range(n)):
                return k + 1
        return -1

