class Solution {
public:
    const int lim = 1010;
    const int inf = 1010;

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        vector<int> dp(lim,0);
        int ans = -1;
        for(int i=0; i<nums.size(); i++){
            for(int j=0; j<lim; j++) dp[j] = inf;
            dp[nums[i]] = 0;
            for(int j=0; j<queries.size(); j++){
                int l = queries[j][0];
                int r = queries[j][1];
                int val = queries[j][2];
                if(!(l <= i && i <= r)) continue;
                for(int k=0; k<=nums[i]-val; k++) if(dp[k+val] != inf) dp[k] = min(dp[k], j+1);
            }
            // cout << dp[0] << ' ';
            ans = max(ans, dp[0]);
        }
        return (ans == inf ? -1 : ans);
    }
};

