class Solution {
    bool fn(vector<vector<int>> &temp, vector<int> &nums){
        for (int i = 0; i < nums.size(); i++) {
            if(nums[i]==0) continue;
            vector<int> dp(nums[i]+1,0);
            dp[0] = 1;
            for(auto c : temp[i]){
                for(int j = nums[i]; j>=c; --j){
                    if(dp[j]==0) dp[j] = dp[j-c];
                }
            }   
            if(dp[nums[i]]==0) return 0;
        }            
        return 1;
    }
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& q) {
        int s = 0, e = q.size(), ans  = -1, n = nums.size();
        while(s<=e){
            int m = (s+e)/2;
            vector<vector<int>> temp(n);
            for (int i = 0; i < m; ++i) {
                for (int j = q[i][0]; j <= q[i][1]; ++j) {
                    temp[j].push_back(q[i][2]);
                }
            }
            if(fn(temp,nums)){
                ans = m;
                e = m-1;
            }
            else s = m+1;
        }
        return ans;
    }
};

