class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = (int)nums.size();
        int Q = (int)queries.size();
        int lo = 0;
        int hi = Q;
        while (lo < hi) {
            int k = (lo + hi) / 2;
            bool allwork = true;
            for (int i = 0; i < n; i++) {
                int num = nums[i];
                vector<bool> dp(num + 1);
                dp[0] = true;
                for (int q = 0; q < k; q++) {
                    if (queries[q][0] <= i && i <= queries[q][1]) {
                        int val = queries[q][2];

                        for (int i = num; i >= val; i--) {
                            dp[i] = dp[i] | dp[i-val];
                        }
                    }
                }
                if (!dp[num]) {
                    allwork = false;
                    break;
                }
            }

            if (allwork) {
                hi = k;
            } else {
                lo = k+1;
            }
        }
        if (lo < Q) return lo;

        bool allwork = true;
        for (int i = 0; i < n; i++) {
            int num = nums[i];
            vector<bool> dp(num + 1);
            dp[0] = true;
            for (int q = 0; q < Q; q++) {
                if (queries[q][0] <= i && i <= queries[q][1]) {
                    int val = queries[q][2];

                    for (int i = num; i >= val; i--) {
                        dp[i] = dp[i] | dp[i-val];
                    }
                }
            }
            if (!dp[num]) {
                allwork = false;
                break;
            }
        }

        if (allwork) return Q;
        return -1;
    }
};

