            if nums[i] == 0:
                return 0

            dct = {nums[i]}
            n = 0

            for l, r, v in queries:
                n += 1
                if l <= i <= r:
                    if v in dct:
                        return n
                    for x in list(dct):                        
                        if (y := x - v) > 0:
