class Solution {
    public int minZeroArray(int[] nums, int[][] q) {
        int ans = -1;
        int n = nums.length;
        int m = q.length;
        for(int i = 0; i < n; i++) {
            int[][] dp = new int[nums[i]+1][m];
            for(int[] x : dp) {
                Arrays.fill(x, 1000000);
            }
            int temp = rec(nums[i], q, 0, dp, i);
            if(temp == 100000) return -1;
            ans = Math.max(ans, temp);
        }
        return ans;
    }

    public int rec(int curr, int[][] q, int idx, int[][] dp, int m) {
        if(curr == 0) return idx;
        if(idx == q.length) return 100000;
        if(dp[curr][idx] != 1000000) return dp[curr][idx];
        dp[curr][idx] = rec(curr, q, idx+1, dp, m);
        if(q[idx][0] <= m && m <= q[idx][1] && curr >= q[idx][2]) {
            dp[curr][idx] = Math.min(dp[curr][idx], rec(curr - q[idx][2], q, idx+1, dp, m));
        }
        return dp[curr][idx];
    }
}

