def fun(n, k, arr):
    # Initialize a 2D DP table with False values.
    dp = [[False for j in range(k + 1)] for i in range(n)]
    for i in range(n):
        dp[i][0] = True
    if arr[0] <= k:
        dp[0][arr[0]] = True

    for ind in range(1, n):
        for target in range(1, k + 1):
            notTaken = dp[ind - 1][target]
            taken = False
            if arr[ind] <= target:
                taken = dp[ind - 1][target - arr[ind]]
            dp[ind][target] = notTaken or taken  
    return dp[n - 1][k]



class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        lo = 0
        hi = len(queries)

        ans = -1
        while(lo<=hi):
            mid = (lo+hi)//2
            def check(mid):
                p = queries[:mid]
                d = defaultdict(list)
                for a,b,val in p:
                    for k in range(a,b+1):
                        d[k].append(val)
                for i in range(len(nums)):
                    req = d[i]
                    if not req:
                        if nums[i]==0:
                            continue
                        else:
                            return False
                    if not fun(len(req),nums[i],req):
                        return False
                return True
            if check(mid):
                ans = mid
                hi = mid-1
            else:
                lo = mid+1
        return ans

