class Solution {
public:
    int dfs(int i, int p, int c, int t, vector<vector<int>>& Q, int q, vector<vector<int>>& M) {
        if(c==t)
        {
            return p;
        }
        if(p==q) 
        {
            return q+1;
        }
        if (M[p][c]!=-1)
        {
            return M[p][c];
        }
        int b=q+1;
        if(Q[p][0]<=i && i<=Q[p][1]) 
        {
            int ns = c+ Q[p][2];
            if (ns<=t) {
                if (ns==t)
                {
                    b=min(b,p+1);
                }
                else 
                {
                    b=min(b,dfs(i,p + 1,ns,t,Q,q,M));
                }
            }
        }
        b = min(b,dfs(i,p+1,c,t,Q,q,M));
        M[p][c] = b;
        return b;
    }

    int solve(int i, int t, vector<vector<int>>& Q, int q) {
        vector<vector<int>> M(q + 1, vector<int>(t + 1, -1));
        return dfs(i, 0, 0, t, Q, q, M);
