class Solution {
public:
    bool is_zero(const vector<set<int>>& nums) {
        for (const auto& s : nums) {
            if (!s.contains(0))
                return false;
        }
        return true;
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int k = 0;

        const int l = nums.size();
        vector<set<int>> nums2(l);
        for (int i = 0; i < l; i++) {
            int v = nums[i];
            nums2[i].insert(v);
        }
        if (is_zero(nums2))
            return k;

        for (auto& v : queries) {
            int li = v[0];
            int ri = v[1];
            int dv = v[2];

            for (int i = li; i <= ri; i++) {
                vector<int> new_vals;
                for (int v : nums2[i]) {
                    if (v >= dv)
                        new_vals.push_back(v - dv);
                }
                for (int v : new_vals)
                    nums2[i].insert(v);
            }
            k++;
            if (is_zero(nums2))
                return k;
        }
        return -1;
    }
};

