class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        @cache
        def solve(index, value, query):
            if value < 0:
                return inf
            if value == 0:
                return query
            if query == len(queries):
                return inf
            k = solve(index, value, query + 1)
            l, r, val = queries[query]
            if l <= index <= r:
                k = min(k, solve(index, value - val, query + 1))
            return k
        k = max((solve(i, nums[i], 0), solve.cache_clear())[0] for i in range(len(nums)))
        return k if k < inf else -1

