                for(int j=1000; j>=d; j--){
                    dp[i][j]|=dp[i][j-d];
                }
            }

            ifz=1;
            for(int i=0; i<n; i++){
                if(!dp[i][nums[i]]){
                    ifz=0;
                }
            }
            ret++;
            if(ifz){
                flag=1;
                break;
            }
        }

        if(flag){
            return ret;
        }
        else{
            return -1;
        }
    }
};

