class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int m = queries.size();
        int lo = 0, hi = m, ans = -1;
        while (lo <= hi) {
            int mid = (lo + hi) / 2;
            bool valid = true;
            int n = nums.size();
            for (int j = 0; j < n; j++) {
                vector<int> vals;
                for (int i = 0; i < mid; i++) {
                    if (j >= queries[i][0] && j <= queries[i][1])
                        vals.push_back(queries[i][2]);
                }
                int target = nums[j];
                if (target == 0)
                    continue;
                int total = 0;
                for (int d : vals)
                    total += d;
                if (total < target) {
                    valid = false;
                    break;
                }
                vector<bool> dp(target + 1, false);
                dp[0] = true;
                for (int d : vals) {
                    for (int k = target; k >= d; k--)
                        dp[k] = dp[k] || dp[k - d];
                }
                if (!dp[target]) {
                    valid = false;
                    break;
                }
            }
            if (valid) {
                ans = mid;
                hi = mid - 1;
            } else {
                lo = mid + 1;
            }
        }
        return ans;
    }
};

