class Solution {
public:
    int calculateEachNum(int val, int idx, vector<vector<int>>& queries) {
        unordered_set<int> mySet;
        mySet.insert(val);

        int ans = 0;
        for(auto &query : queries) {
            ans++;

            if(query[0] > idx || query[1] < idx) {
                continue;
            }

            auto decrement = query[2];
            auto toAdd = vector<int>();

            for(auto i : mySet) {
                if(i - decrement == 0) {
                    return ans;
                } else if (i - decrement > 0) {
                    toAdd.push_back(i - decrement);
                }
            }

            for(auto i : toAdd) {
                mySet.insert(i);
            }
        }

        return -1;
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int ans = 0;

        for(int idx = 0; idx < nums.size(); idx++) {
            auto val = nums[idx];
            if(val == 0) continue;

            auto t = calculateEachNum(val, idx, queries);
            if(t < 0) {
                return -1;
            }

            ans = max(ans, t);
        }

        return ans;
    }
};

