class Solution {
public:
    bitset<10001> dp;
    bool canSum(vector<int>& f, int t){
        if(t>10000) return false;
        long long s=0;
        for(int i=1;i<=10;i++) s+=1LL*i*f[i];
        if(s<t) return false;
        dp.reset();
        dp[0]=1;
        for(int i=1;i<=10;i++){
            int c=f[i],base=1;
            while(c>0){
                int use=min(base,c);
                int cost=use*i;
                dp|=(dp<<cost);
                c-=use;
                base<<=1;
            }
        }
        return dp[t];
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(), m = queries.size();

        //algorithm
        for(int x : nums){
            if(x < 0 && x != 0) return -1;
        }
        bool allZero = true;
        for(int x : nums){
            if(x != 0){
                allZero = false;
                break;
            }
        }
        if(allZero) return 0;

        //ultinate algorithm
        vector<vector<int>> freq(n,vector<int>(11,0));
        for(int i=0; i<m; i++){
            int l = queries[i][0], r = queries[i][1], v = queries[i][2];
            for(int j=l; j<=r; j++) freq[j][v]++;
            bool flag = true;
            for(int j=0; j<n; j++){
                if(nums[j] > 0){
                    if(!canSum(freq[j], nums[j])){
                        flag = false;
                        break;
                    }
                }
            }
            if(flag) return i+1;
        }
        return -1;
    }
};

