class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        // for each value, find the minimum number of queries we can use to reduce it
        // O(n * max(nums[i]) * queries)
        // O(queries * n * )
        int n = nums.size(), k = 0;
        for (int i = 0; i < n; i++) {
            if (nums[i] == 0)
                continue;

            set<int> visited;
            visited.insert(0);

            for (int j = 0; j < queries.size(); j++) {
                if (queries[j][0] > i || queries[j][1] < i)
                    continue;

                set<int> nextVisited;
                for (auto it = visited.rbegin(); it != visited.rend(); it++) {
                    nextVisited.insert(*it + queries[j][2]);
                    nextVisited.insert(*it);
                }

                visited = nextVisited;

                if (visited.find(nums[i]) != visited.end()) {
                    k = max(k, j + 1);
                    break;
                }
            }

            if (visited.find(nums[i]) == visited.end())
                return -1;
        }

        return k;
    }
};

