class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int m = queries.size();
        vector<int> required_k(n, -1);

        for (int j = 0; j < n; ++j) {
            int target = nums[j];
            if (target < 0) return -1;
            if (target == 0) {
                required_k[j] = -1;
                continue;
            }

            vector<pair<int, int>> Q_j;
            for (int i = 0; i < m; ++i) {
                int l = queries[i][0];
                int r = queries[i][1];
                int val = queries[i][2];
                if (l <= j && j <= r) {
                    Q_j.emplace_back(i, val);
                }
            }

            if (Q_j.empty()) {
                return -1;
            }

            sort(Q_j.begin(), Q_j.end());
            unordered_map<int, int> dp;
            dp[0] = -1;

            for (auto& [i, val] : Q_j) {
                unordered_map<int, int> new_dp = dp;
                for (auto& [s, max_idx] : dp) {
                    int new_s = s + val;
                    if (new_s > target) continue;
                    int new_max = max(max_idx, i);
                    if (new_dp.find(new_s) == new_dp.end() || new_max < new_dp[new_s]) {
                        new_dp[new_s] = new_max;
                    }
                }
                dp.swap(new_dp);
            }

            if (dp.find(target) == dp.end()) {
                return -1;
            }
            required_k[j] = dp[target];
        }

        int ans = 0;
        for (int j = 0; j < n; ++j) {
            if (nums[j] == 0) continue;
            int current_k = required_k[j] + 1;
            ans = max(ans, current_k);
        }

        if (ans == 0) {
            for (int num : nums) {
                if (num != 0) return -1;
            }
            return 0;
        }

        return ans;
    }
};

