from typing import List

class Solution:
    def minZeroArray(self, arr: List[int], ops: List[List[int]]) -> int:
        size = len(arr)
        op_count = len(ops)
        def check_zero(k: int) -> bool:
            idx = 0
            while idx < size:
                goal = arr[idx]
                op_vals = []
                q = 0
                while q < k:
                    a, b, c = ops[q]
                    if a <= idx <= b:
                        op_vals.append(c)
                    q += 1
                achievable = [False] * (goal + 1)
                achievable[0] = True
                v = 0
                while v < len(op_vals):
                    cur = op_vals[v]
                    s = goal
                    while s >= cur:
                        if achievable[s - cur]:
                            achievable[s] = True
                        s -= 1
                    v += 1
                if not achievable[goal]:
                    return False
                idx += 1
            return True

        low_bound = 0
        high_bound = op_count
        result = -1
        while low_bound <= high_bound:
            mid = (low_bound + high_bound) // 2
            if check_zero(mid):
                result = mid
                high_bound = mid - 1
            else:
                low_bound = mid + 1
        return result

