class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {

        int[][] query = queries;

        int low = 0, high = queries.length;
        int result = -1;

        while (low <= high) {
            int mid = low + (high - low) / 2;
            if (canSatisfy(nums, query, mid)) {
                result = mid;
                high = mid - 1;
            } else {
                low = mid + 1;
            }
        }

        return result;
    }

    private boolean canSatisfy(int[] nums, int[][] queries, int k) {
        int n = nums.length;

        for (int i = 0; i < n; i++) {
            int target = nums[i];

            ArrayList<Integer> values = new ArrayList<>();
            for (int j = 0; j < k; j++) {
                int l = queries[j][0], r = queries[j][1], val = queries[j][2];
                if (i >= l && i <= r) {
                    values.add(val);
                }
            }

            boolean[] dp = new boolean[target + 1];
            dp[0] = true;
            for (int val : values) {
                for (int s = target; s >= val; s--) {
                    dp[s] = dp[s] || dp[s - val];
                }
            }
            if (!dp[target]) {
                return false;
            }
        }
        return true;
    }
}

