class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        # determine for each element, what's the minium k such that element becomes 0
        n = len(nums)
        kArr = [-1 for _ in range(n)]
        for i in range(n):
            if nums[i] == 0:
                kArr[i] = 0
