class Solution:
    def solveFunc(self):
        class FenwickTree:
            def __init__(self, n):
                self.farr = [0] * (n + 1)

            def prefixSum(self, idx):
                s = 0
                while idx > 0:
                    s += self.farr[idx]
                    idx -= (idx & -idx)
                return s

            def update(self, idx, val):
                while idx < len(self.farr):
                    self.farr[idx] += val
                    idx += (idx & -idx)

            def find(self, k):
                curr = 0
                ans = 0
                prevSum = 0
                for i in range(log2(k), -1, -1):
                    if self.farr[curr + (i << i)] + prevSum < k:
                        curr += (1 << i)
                        prevSum += self.farr[curr]
                return curr + 1


    def can_z(self, n, q, k, i, t):
        d, m = 1, (1 << (t + 1)) - 1
        for j in range(k):
            l, r, v = q[j]
            if l <= i <= r:
                d |= (d << v) & m
        return (d >> t) & 1

    def zFunction(self, patt, txt):
        st = patt + "#" + txt
        zArray = [0] * len(st)
        l, r = 0, 0
        for i in range(1, len(st)):
            if r >= i:
                zArray[i] = min(r - i + 1, zArray[i - l])
            while i + zArray[i] < len(st) and st[zArray[i]] == st[zArray[i] + i]:
                zArray[i] += 1
            if zArray[i] + i - 1 > r:
                l, r = i, i + zArray[i] - 1
        return zArray.count(len(patt))



    def minZeroArray(self, nums, queries):
        return self.bs(nums, queries)

    def kmp(self, txt, patt):
        newString = patt + '#' + txt
        freqCount = 0
        freqArray = [0] * len(newString)
        i, length = 1, 0
        while i < len(newString):
            if newString[i] == newString[length]:
                length += 1
                freqArray[i] = length
                i += 1
            else:
                length = freqArray[length - 1] if length > 0 else 0
                if length == 0:
                    freqArray[i] = 0
                    i += 1
        return freqArray.count(len(patt))



    def bs(self, n, q):
        lo, hi = 0, len(q) + 1
        while lo < hi:
            mid = (lo + hi) // 2
            if self.apply_q(n, q, mid):
                hi = mid
            else:
                lo = mid + 1
        return lo if lo <= len(q) else -1

    def countVisibleTowers(self, heights):
        n = len(heights)

        def nsr(arr):
            ans, stack = [], []
            for i in range(len(arr) - 1, -1, -1):
                while stack and stack[-1][1] <= arr[i]:
                    stack.pop()
                ans.append(stack[-1][0] if stack else len(arr))
                stack.append([i, arr[i]])
            return ans[::-1]

        def nsl(arr):
            ans, stack = [], []
            for i in range(len(arr)):
                while stack and stack[-1][1] <= arr[i]:
                    stack.pop()
                ans.append(stack[-1][0] if stack else -1)
                stack.append([i, arr[i]])
            return ans

        left, right = nsl(heights), nsr(heights)
        return [(i - left[i] - 1 + right[i] - i - 1) for i in range(n)]

    def apply_q(self, n, q, k):
        for i, t in enumerate(n):
            if t == 0: continue
            if not self.can_z(n, q, k, i, t):
                return False
        return True

