class Solution {
public:
  int minZeroArray(vector<int>& a, vector<vector<int>>& b) {
    int n = (int) a.size();
    if (count(a.begin(), a.end(), 0) == n) {
      return 0;
    }
    int m = (int) b.size();
    vector<vector<pair<int, int>>> ops(n);
    for (int j = 0; j < m; j++) {
      for (int k = b[j][0]; k <= b[j][1]; k++) {
        ops[k].emplace_back(b[j][2], j);
      }
    }
    const int INF = (int) 1e9;
    int ans = -1;
    auto Work = [&](int x, vector<pair<int, int>>& a) -> int {
      if (x == 0) {
        return 0;
      }
      if ((int) a.size() == 0) {
        return INF;
      }
      int n = (int) a.size();
      vector<vector<bool>> dp(n + 1, vector<bool>(x + 1));
      dp[0][0] = true;
      for (int i = 0; i < n; i++) {
        for (int j = 0; j <= x; j++) {
          if (dp[i][j]) {
            dp[i + 1][j] = true;
            if (j + a[i].first <= x) {
              dp[i + 1][j + a[i].first] = true;
            }
          }
        }
      }
      for (int i = 0; i <= n; i++) {
        if (dp[i][x]) {
          assert(i > 0);
          return a[i - 1].second;
        }
      }
      return INF;
    };
    for (int i = 0; i < n; i++) {
      ans = max(ans, Work(a[i], ops[i]));
    }
    return ans == INF ? -1 : ans + 1;
  }
};

