class Solution {
public:
    int minZeroArray(vector<int>& a, vector<vector<int>>& q) {
        int n = a.size();
        bool cc = false;
        for(auto &x : a)if(x)cc = true;
        if(!cc)return 0;
        int lo = 0, hi = q.size()-1, mid;
        while(lo<=hi) {
            mid = (lo+hi)/2;
            vector<vector<int>> add(n+1);
            for(int i=0; i<=mid; i++) {
                for(int j = q[i][0]; j<=q[i][1]; j++)add[j].push_back(q[i][2]);
            }
            auto find = [&](int val, vector<int> &temp) -> bool {
                vector<vector<int>> dp(1+temp.size(), vector<int>(val+1));
                for(int i = 1; i<=temp.size(); i++) {
                    dp[i-1][val] = 1;
                    for(int j = 0; j<=val; j++) {
                        dp[i][j] = dp[i-1][j];
                        if(temp[i-1]+j<=val)dp[i][j] = max(dp[i-1][j+temp[i-1]], dp[i][j]);
                    }
                }
                return (dp[temp.size()][0]>=1);
            };

            bool f = true;
            for(int i = 0; i<n; i++) {
                if(!find(a[i], add[i])) {
                    f=false;
                    break;
                }
            }
            if(f) {
                hi = mid-1;
            } else lo = mid+1;
        }
        if(lo == q.size())return -1;
        return lo+1;
    }

};

