from typing import List

class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        ans, lf, rg = -1, 0, len(queries)
        while lf <= rg:
            mid = (lf + rg) // 2
            if self._feasible(nums, queries, mid):ans,rg = mid, mid - 1
            else:lf = mid + 1
        return ans
    def _feasible(self, nums: List[int], queries: List[List[int]], k: int) -> bool:
        n = len(nums)
        cose = [[] for _ in range(n)]
        list(map(lambda q: [cose[i].append(q[2]) for i in range(q[0], q[1] + 1)], queries[:k]))
        for i in range(n):
            if not self._subset_sum_possible(cose[i], nums[i]): return False
        return True
    def _subset_sum_possible(self, arr: List[int], target: int) -> bool:
        ach = {0}
        for x in arr:
            nach = set(ach)
            for s in ach:
                s2 = s + x
                if s2 == target:return True
                if s2 < target:nach.add(s2)
            ach = nach
        return (target in ach)

