class Solution {
  // needs to compute possible range
  // n <= 10, val <= 1000, qs.length <= 1000
  // for each index, do the computation
  // do a dp
  public int minZeroArray(int[] nums, int[][] qs) {
    int n = nums.length;

    int res = 0;
    for (int i = 0; i < n; i++) {
      int tmp = compute(i, nums[i], qs);
      if (tmp == -1) return tmp;

      res = Math.max(res, tmp);
    }
    return res;
  }

  private int compute(int idx, int val, int[][] qs) {
    if (val == 0) return 0;

    boolean[] dp = new boolean[val + 1];
    dp[val] = true;

    for (int i = 0; i < qs.length; i++) {
      int[] q = qs[i];
      if (q[1] < idx || q[0] > idx) continue;

      boolean[] cur = dp.clone();
      for (int j = val; j >= q[2]; j--) if (dp[j]) {
        cur[j - q[2]] = true;
      }
      if (cur[0]) return i + 1;

      dp = cur;
    }
    return -1;
  }
}

