class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        bool all_zero = true;
        for (int num : nums) {
            if (num != 0) {
                all_zero = false;
                break;
            }
        }
        if (all_zero) {
            return 0;
        }
        int min_queries = 0;
        vector<bool> possible_values(1005, false);
        for (int num_idx=0; num_idx < nums.size(); ++num_idx) {
            int num = nums[num_idx];
            // how many queries until we can reach 0?
            std::fill(possible_values.begin(), possible_values.end(), false);
            possible_values[num] = true;
            for (int k=0; k < queries.size(); ++k) {
                int left = queries[k][0];
                int right = queries[k][1];
                int val = queries[k][2];
                if (left <= num_idx && num_idx <= right) {
                    for (int j=val; j <= num; ++j) {
                        if (possible_values[j]) {
                            possible_values[j-val] = true;
                        }
                    }
                }
                if (possible_values[0]) {
                    min_queries = max(min_queries, k+1);
                    break;
                }
            }
            if (!possible_values[0]) {
                // never possible
                return -1;
            }
        }
        return min_queries;
    }
};

