class Solution:
    def minZeroArray(self, nums, queries):
        if all(x==0 for x in nums): return 0
        n=len(nums)
        c=[[] for _ in range(n)]
        for i, (l, r, v) in enumerate(queries):
            for j in range(l, r+1):
                c[j].append((i, v))
        for j in range(n):
            c[j].sort(key=lambda x: x[0])
        b=[1 for _ in range(n)]
        p=[0] * n
        for k in range(len(queries) + 1):
            ok = True
            for j in range(n):
                while p[j] < len(c[j]) and c[j][p[j]][0] < k:
                    v = c[j][p[j]][1]
                    b[j] = b[j] | (b[j] << v)
                    p[j] += 1
                if nums[j] < 0 or nums[j] >= b[j].bit_length() or not(b[j] & (1<<nums[j])):
                    ok = False
                    break
            if ok: return k

        return -1

