import java.util.*;

class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        int m = queries.length;
        List<List<int[]>> elementQueries = new ArrayList<>();

        for (int i = 0; i < n; i++) {
            elementQueries.add(new ArrayList<>());
        }

        for (int qIdx = 0; qIdx < m; qIdx++) {
            int[] q = queries[qIdx];
            int l = q[0];
            int r = q[1];
            int val = q[2];
            for (int i = l; i <= r; i++) {
                elementQueries.get(i).add(new int[]{qIdx, val});
            }
        }

        int[] k_i = new int[n];
        Arrays.fill(k_i, -1);

        for (int i = 0; i < n; i++) {
            if (nums[i] == 0) {
                continue;
            }

            List<int[]> qList = elementQueries.get(i);
            Map<Integer, Integer> dp = new HashMap<>();
            dp.put(0, -1);

            for (int[] q : qList) {
                int qIdx = q[0];
                int val = q[1];
                Map<Integer, Integer> newDp = new HashMap<>(dp);

                for (Map.Entry<Integer, Integer> entry : dp.entrySet()) {
                    int s = entry.getKey();
                    int currentK = entry.getValue();
                    int newS = s + val;

                    if (newS > nums[i] || val > (nums[i] - s)) {
                        continue;
                    }

                    int newK = Math.max(currentK, qIdx);
                    if (!newDp.containsKey(newS) || newK < newDp.get(newS)) {
                        newDp.put(newS, newK);
                    }
                }

                dp = newDp;
            }

            if (dp.containsKey(nums[i])) {
                k_i[i] = dp.get(nums[i]);
            } else {
                return -1;
            }
        }

        int maxK = -1;
        for (int k : k_i) {
            maxK = Math.max(maxK, k);
        }

        if (maxK == -1) {
            return 0;
        } else {
            return maxK + 1;
        }
    }
}

