class Solution {
public:
  int minZeroArray(vector<int> &a, vector<vector<int>> &q) {
    int n = a.size(), m = q.size();
    int lb = -1, rb = m + 1;
    while (rb - lb > 1) {
      int mb = (lb + rb) >> 1;
      vector<vector<int>> d(n + 1);
      vector<int> cur;
      for (int i = 0; i < mb; i++) {
        d[q[i][0]].push_back(q[i][2]);
        d[q[i][1] + 1].push_back(-q[i][2]);
      }
      bool ok = true;
      multiset<int> st;
      for (int i = 0; i < n; i++) {
        for (int v : d[i]) {
          if (v < 0) {
            st.erase(st.find(-v));
          } else {
            st.insert(v);
          }
        }
        vector<int> dp(a[i] + 1);
        dp[a[i]] = true;
        for (int v : st) {
          for (int sum = v; sum <= a[i]; sum++) {
            dp[sum - v] |= dp[sum];
          }
        }
        ok &= dp[0];
      }
      if (ok) {
        rb = mb;
      } else {
        lb = mb;
      }
    }
    return rb > m ? -1 : rb;
  }
};

