class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:        
        n, m = len(nums), len(queries)
        l, r = 0, m + 1

        def possible(k):
            if not k:
                return all(not num for num in nums)

            for i, num in enumerate(nums):
                candidates = []
                for j in range(k):
                    start, end, delta = queries[j]
                    if start <= i <= end:
                        candidates.append(delta)

                candidates.sort()
                t = len(candidates)

                dp = [[False] * (num + 1) for _ in range(t + 1)]
                dp[0][0] = True

                for x in range(1, t + 1):
                    for y in range(num + 1):
                        dp[x][y] |= dp[x - 1][y]
                        if y - candidates[x - 1] >= 0:
                            dp[x][y] |= dp[x - 1][y - candidates[x - 1]]

                if not dp[t][num]:
                    return False

            return True

        while l < r:
            mid = (l + r) // 2
            if not possible(mid):
                l = mid + 1
            else:
                r = mid

        if l == m + 1:
            return -1
        return l

