class Solution {
vector<vector<int>> dp;
int solve(int index, int el, vector<pair<int,int>> &v){
    if(el == 0) return 0;
    if (index == v.size() || el < 0) return 1e9;

    if(dp[index][el] != -1) return dp[index][el];
        int cur = v[index].first;
    if(cur == el){
        return dp[index][el] = v[index].second;
    }
    int ans = 1e9;
    if(cur < el) ans = min(ans, solve(index+1, el-cur, v));
    ans = min(ans, solve(index+1, el, v));
    return dp[index][el] = ans;
}
