    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        map<int, vector<pair<int,int>>> mpp;

        int cnt = 1;
        for(auto &it: queries){
            for(int i=it[0]; i<=it[1]; i++){
                mpp[i].push_back({it[2], cnt});
