        int ans = -1;
        for(auto &it: mpp){
            int max_el = nums[it.first]; 
            dp.assign(it.second.size(), vector<int>(max_el + 1, -1)); 
            ans = max(ans, solve(0, nums[it.first], it.second));
        }

        for(int i=0; i<nums.size(); i++){
            if(mpp.find(i) ==mpp.end()){
                if(nums[i] != 0) ans = 1e9;
                break;
            }
        }
        if(ans >= 1e9) return -1;
        return ans;
    }
};

