        int[][] cnt = new int[n][11];
        for (int i = 0; i < k; i++)
            for (int j = qs[i][0]; j <= qs[i][1]; j++)
                cnt[j][qs[i][2]]++;
        for (int i = 0; i < n; i++)
            if (!g(cnt[i], nums[i]))
                return false;
        return true;
    }
    public int minZeroArray(int[] nums, int[][] qs) {
        int m = qs.length, p = 0, q = m;
        while (p < q) {
            int mid = p + (q - p) / 2;
            if (f(nums, qs, mid))
                q = mid;
            else
                p = mid + 1;
        }
        return f(nums, qs, p) ? p : -1;
    }
}

