class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int q = queries.size();
        int n = nums.size();

        int lo = 0, hi = q + 1;
        while (lo < hi) {
            int mid = lo + (hi - lo) / 2;
            if (canAchieve(nums, queries, mid))
                hi = mid;
            else
                lo = mid + 1;
        }
        return (lo > q ? -1 : lo);
    }
    bool canAchieve(const vector<int>& nums, const vector<vector<int>>& queries, int k) {
        int n = nums.size();
        for (int i = 0; i < n; i++) {
            int target = nums[i];
            if (target == 0)
                continue;

            vector<int> relevant;
            for (int j = 0; j < k; j++) {
                if (queries[j][0] <= i && i <= queries[j][1]) {
                    relevant.push_back(queries[j][2]);
                }
            }

            unordered_map<int, unordered_map<int, bool>> memo;
            if (!subsetSum(relevant, 0, 0, target, memo))
                return false;
        }
        return true;
    }

    bool subsetSum(vector<int>& vals, int index, int curr, int target,
                   unordered_map<int, unordered_map<int, bool>>& memo) {
        if (curr == target)
            return true;
        if (index >= vals.size() || curr > target)
            return false;

        if (memo.count(index) && memo[index].count(curr))
            return memo[index][curr];

        bool res = subsetSum(vals, index + 1, curr, target, memo);
        if (curr + vals[index] <= target)
            res = res || subsetSum(vals, index + 1, curr + vals[index], target, memo);

        memo[index][curr] = res;
        return res;
    }
};

