class Solution {
public:

    bool f(const std::vector<int>& arr, int n, int sum) {
    // Create a boolean array to store results of subproblems
    std::vector<std::vector<bool>> dp(n + 1, std::vector<bool>(sum + 1));

    // If sum is 0, then answer is true (empty subset)
    for (int i = 0; i <= n; i++) {
        dp[i][0] = true;
    }

    // Fill the dp array
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= sum; j++) {
            // If current element is greater than sum, ignore it
            if (arr[i - 1] > j) {
                dp[i][j] = dp[i - 1][j];
            } else {
                // Check if sum can be obtained by including or excluding the current element
                dp[i][j] = dp[i - 1][j] || dp[i - 1][j - arr[i - 1]];
            }
        }
    }

    return dp[n][sum];
}




    int minZeroArray(vector<int>& a, vector<vector<int>>& q) {
