        for(auto i:a){
            if(i > 0)flagg=false;
        }
        if(flagg)return 0;


        while(l<=h){
            int m=l+h;
            m>>=1;

            map<int,vector<int>> mp;
            int c=m;
            for(auto i:q){
                int f=i[0],s=i[1],val=i[2];
                for(int j=f;j<=s;j++){
                    mp[j].push_back(val);
                }
                c--;
                if(c == 0)break;
            }

            bool flag=true;
            for(int i=0;i<a.size();i++){
                if(f(mp[i],mp[i].size(),a[i]))continue;
                flag=false;
            }

            if(flag){
                ans=m;
                h=m-1;
            }else{
                l=m+1;
            }
        }

        return ans;
    }
};

