public:
    int f(int target,int j,int i,vector<vector<int>>& q,int m,vector<vector<int>>&dp)
    {
        if(i==m)
        return m;

        if(dp[target][i]!=-1)
        return dp[target][i];

        int ans=f(target,j,i+1,q,m,dp);
        if(q[i][0]<=j && q[i][1]>=j && target>=q[i][2])
        {
            if(target==q[i][2])
            ans=i;

            ans=min(ans,f(target-q[i][2],j,i+1,q,m,dp));
        }
        return dp[target][i]=ans;
