#include <vector>
#include <unordered_map>
using namespace std;

class Solution {
public:
    int f(vector<int>& a, vector<vector<pair<int, int>>>& v, int i) {
        if (a[i] == 0) return 0;
        auto& q = v[i];
        if (q.empty()) return -1;
        unordered_map<int, int> d;
        d[0] = -1;
        for (auto& [j, x] : q) {
            unordered_map<int, int> t = d;
            for (auto& [s, k] : d) {
                int ns = s + x, nk = max(k, j);
                if (!t.count(ns) || nk < t[ns]) t[ns] = nk;
            }
            swap(d, t);
        }
        return d.count(a[i]) ? d[a[i]] + 1 : -1;
    }

    int g(vector<int>& a) {
        for (int x : a) if (x) return 0;
        return 1;
    }

    int minZeroArray(vector<int>& a, vector<vector<int>>& q) {
        int n = a.size(), m = q.size(), r = 0;
        vector<vector<pair<int, int>>> v(n);
        for (int j = 0; j < m; ++j) 
            for (int i = q[j][0]; i <= q[j][1]; ++i) 
                v[i].emplace_back(j, q[j][2]);
        for (int i = 0; i < n; ++i) {
            int t = f(a, v, i);
            if (t == -1) return -1;
            r = max(r, t);
        }
        return r ? r : (g(a) ? 0 : -1);
    }
};

