class Solution {
public:

    bool check(vector<vector<int>>& queries, vector<int> nums, int k){
        //for first k queries, at each index what are the possible answers
        vector<unordered_set<int>> possible(nums.size());
        for(int i = 0; i < nums.size(); ++i){
            possible[i].insert(nums[i]);
        }
        for(int i = 0; i < k; ++i){
            int a = queries[i][0];
            int b = queries[i][1];
            int val = queries[i][2];
            for(int j = a; j <= b; ++j){
                vector<int> add;
                for(auto &x:possible[j]){
                    if(x-val >= 0){
                        add.push_back(x-val);
                    }
                }
                for(auto &temp:add){
                    possible[j].insert(temp);
                }

            }
        }
        // for(auto &x:possible){
        //     for(auto &y:x){
        //         cout << y << " ";
        //     }
        //     cout << endl;
        // }
        // cout << endl;
        for(int i = 0; i < nums.size(); ++i){
            if(!possible[i].count(0))return false;
        }
        return true;
    }

    int binSearch(int l, int r, vector<vector<int>>& queries, vector<int> nums){
        if(l > r)return -1;
        int mid = (l+r)/2;
        if(!check(queries, nums, mid)){
            //try more
            return binSearch(mid+1, r, queries, nums);
        }else{
            int temp = binSearch(l, mid-1, queries, nums);
            if(temp != -1)return temp;
            return mid;
        }
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        //apply all queries
        return binSearch(0, (int)queries.size(), queries, nums);
    }
};

