class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        int m = queries.length;
        int ans = 0;
        for (int i=0; i<n; i++) {
            Set<Integer> s = new HashSet<>();
            s.add(0);
            int j = 0;
            while (j < m && !s.contains(nums[i])) {
                if (queries[j][0] <= i && queries[j][1] >= i) {
                    Set<Integer> t = new HashSet<>();
                    for (int k : s) {
                        t.add(k);
                        int z = k + queries[j][2];
                        if (z <= nums[i])
                            t.add(z);
                    }
                    s = t;
                }
                j++;
            }
            if (!s.contains(nums[i]))
                return -1;
            ans = Math.max(ans, j);
        }
        return ans;
    }
}

