class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        total_queries = len(queries)
        ways = [set([0]) for _ in range(len(nums))]
        columns = set()
        for i, v in enumerate(nums):
            if v != 0:
                columns.add(i)

        i = 0
        while i < total_queries:
            if not columns:
                return i

            l, r, v = queries[i]
            for col in range(l, r + 1):
                if col not in columns:
                    continue

                for x in list(ways[col]):
                    if x + v == nums[col]:
                        columns.discard(col)
                        break

                    ways[col].add(x + v)

            i += 1

        if columns:
            return -1
        else:
            return total_queries

