class Solution {
public:
bool canZeroArray(const vector<int>& nums, const vector<vector<int>>& queries, int k) {
    int n = nums.size();
    for (int j = 0; j < n; j++) {
        int target = nums[j];
        vector<bool> dp(target + 1, false);
        dp[0] = true;
        for (int i = 0; i < k; i++) {
            int l = queries[i][0], r = queries[i][1], v = queries[i][2];
            if (j >= l && j <= r) { 
                for (int t = target; t >= v; t--) {
                    if (dp[t - v])
                        dp[t] = true;
                }
            }
        }
        if (!dp[target])
            return false;
    }
    return true;
}
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int q = queries.size();
        int ans = -1;
        for (int k = 0; k <= q; k++) {
            if (canZeroArray(nums, queries, k)) {
                ans = k;
                break;
            }
        }
        return ans;
    }
};

