class Solution {
public:
  int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
    auto test = [&](int k) {
      for (int i = 0; i < nums.size(); ++i) {
        int x = nums[i];
        vector<bool> a(x + 1);
        a[0] = true;
        for (int u = 0; u < k; ++u) {
          const auto& q = queries[u];
          if (q[0] <= i && i <= q[1]) {
            int y = q[2];
            for (int j = x - y; j >= 0; --j) if (a[j]) a[j + y] = true;
            if (a[x]) break;
          }
        }
        if (!a[x]) return false;
      }
      return true;
    };

    int lo = 0, hi = queries.size() + 1;
    while (lo < hi) {
      int mid = (lo + hi) / 2;
      if (test(mid)) hi = mid; else lo = mid + 1;
    }
    return lo > queries.size() ? -1 : lo;
  }
};

