class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length, m = queries.length;
        int[] w = new int[n];
        Arrays.fill(w, -1);

        for (int i = 0; i < n; i++) {
            int x = nums[i];
            if (x == 0) {
                w[i] = 0;
                continue;
            }
            boolean[] f = new boolean[x + 1];
            f[0] = true;
            for (int j = 0; j < m; j++) {
                int l = queries[j][0], r = queries[j][1], v = queries[j][2];
                if (i >= l && i <= r) {
                    for (int k = x - v; k >= 0; k--) {
                        if (f[k] && k + v <= x) f[k + v] = true;
                    }
                }
                if (f[x]) {
                    w[i] = j + 1;
                    break;
                }
            }
            if (w[i] == -1) return -1;
        }
        int res = 0;
        for (int x : w) res = Math.max(res, x);
        return res;
    }
}

