class Solution {
public:
    int minZeroArray(vector<int>& v, vector<vector<int>>& q) {
        int n = v.size(), m = q.size();
        bool k = true;
        if (count(v.begin(), v.end(), 0) == n) {
            return 0;
        }

        auto helper = [&](int k) -> bool {
            for (int i = 0; i < n; i++) {
                vector<int> dp(v[i] + 1, false);
                dp[0] = true;
                for (int j = 0; j < k; j++) {
                    int res = q[j][2];
                    if (q[j][0] <= i && i <= q[j][1]) {
                        for (int p = v[i] - res; p >= 0; p--) {
                            if (dp[p]) {
                                dp[p + res] = true;
                            }
                        }
                    }
                }
                if (!dp[v[i]]) {
                    return false;
                }
            }
            return true;
            };

        int low = 1, high = m, ans = -1;
        while (low <= high) {
            int mid = low + (high - low) / 2;
            if (helper(mid)) {
                ans = mid;
                high = mid - 1;
            }
            else {
                low = mid + 1;
            }
        }
        return ans;
    }
};

