class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        Q = len(queries)

        @cache
        def go(x, i, q):
            if x == 0:
                return q
            if q == Q:
                return inf

            # skip
            best = go(x, i, q + 1)
            l, r, v = queries[q]
            if l <= i <= r and v <= x:
                best = min(best, go(x - v, i, q + 1))

            return best

        best = 0
        for i, n in enumerate(nums):
            r = go(n, i, 0)
            # print(i, n, r)
            if r == inf:
                return -1
            best = max(r, best)
        go.cache_clear()
        return best

