class DP {
public:
    //01    
    int64 dp( int M , vector< int > &info  ) {

        int n = info.size() ; 
        vector<int64> f ( M + 1 , 0 ) ; 
        f[0] = 1 ; 
        for( int i = 0 ; i < n ; i++ ) { 
            auto  W = info[i] ; 

            for( int j = M ; j >= W ; --j ) {
                if( f[j-W]  ) {
                    f[j] = 1 ; 
                }
            }
        }
        return f[ M ] ; 
    }
};

class Solution {
public:
    vector<int> a ; 
    int n ; 
    vector< vector<int>> info ; 
    vector<vector<int>> queries ; 
    int m ; 

    void init(  int tm  ) { 
        info.assign( n , {} ) ; 
        for( int i = 0 ; i <= tm ; i++ ) {
            auto &p = queries[i] ;
            int L = p[0] , R = p[1] , val = p[2] ; 

            for( int j = L ; j <= R ; j++ ) {
                info[j].emplace_back( val ) ;
            }
        }
    }

    bool check( int mid ) {

        init( mid ) ; 

        for( int i = 0 ; i < n ; i++ ) {
            int val = a[i] ; 
            DP t ; 
            auto ret = t.dp( val , info[i] ) ; 
            if( ! ret ) {
                return false ; 
            }
        }
        return true ; 
    }
    int solve( ) { 

        int low = 0 ,  high = m - 1 ; 
        int ret = -1 ; 
        while( low <= high ) {
            int mid = ( low + high ) >> 1 ; 
            if( check( mid )) { 
                ret = mid ; 
                high = mid - 1 ; 
            }
            else {
                low = mid + 1 ; 
            }
        }
        if( ret == -1 ) {
            return -1 ;
        }
        return ret + 1 ; 
    }
    bool is_all0() {
        for( int i = 0 ; i < n ; i++ ) {
            if( a[i] != 0 ) {
                return false  ; 
            }
        }
        return true ; 
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        a = nums ; 
        n = a.size() ; 

        if( is_all0()) { 
            return 0 ;
        }


        this->queries = queries ; 
        m = queries.size() ; 

        return solve() ; 
    }
};

